import React, {Component} from 'react';
import {confirmAlert} from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';

class Tableau extends Component {

  //  EditUserForm = props => {
  //    const [ user, setUser ] = useState(this.props.currentUser);

  //    const handleInputChange = event => {
  //      const { name, value } = event.target;

  //      setUser({ ...user, [name]: value });
  //  };
   changed = event => {
    const { Prix, value } = event.target
    setUser({ ...user, [Prix]: value })
  }


  submit = (e,a) => {
    confirmAlert({
      title: '',
      message:"Suppression Produit"+a ,
      buttons: [
        {
          label: 'OUI',
          onClick: () => this.props.suppr(e)
        },
        {
          label: 'NON',
          onClick: () => console.log('suppression refusée')
        }
      ],

    })
  }

  submit2 = (user) => {
    confirmAlert({
      customUI: ({ onClose }) => {
        return (
          <div className='custom-ui '>
              <form


              >
                  <input type="Text" value={user.Prix} onChange={changer}></input>

                    <button onClick={()=>{
                      this.props.editRow(user)
                       ;onClose()} 
                    }>

                      OK
                    </button>
                    <button onClick={() => {
                        onClose()
                    }}>Annuler</button>
              </form>

          </div>
        )
      }
    })
  }

  render() {
    return(
      <div>
      <table className="table table-hover table-bordered">
      <thead>
        <tr className="table-primary">
          <th scope="col" className="act">id</th>
          <th scope="col">Produit</th>
          <th scope="col">Prix</th>
          <th scope="col" className="act">Actions</th>
        </tr>
      </thead>
      <tbody>
        {this.props.users.length > 0 ? (
          this.props.users.map(user => (
            <tr>
              <td>{user.id}</td>
              <td>{user.Produits}</td>
              <td>{user.Prix}</td>
              <td>
              <label>
          <button aria-label="Supprimer" id="suppr"
            onClick={() => this.submit(user.id,user.Produits)} className ="btn btn-danger" >X</button>


            <button className ="btn btn-success"
            onClick={() => this.submit2(user)}
            >Edit</button>
            </label>
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan={4}>Aucun enregistrement</td>
          </tr>
        )}
      </tbody>
      </table>
      <div>TOTAL</div>
      <div >TOTAL</div>
  </div> 
      )
      }
  }



export default Tableau;
