import React, { Component } from 'react'
import axios from 'axios'
import { Redirect } from "react-router-dom";
import "./home.css"

export default class home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            product: "",
            url:""
        }
    }


    getAll() {
        axios.get("http://localhost:8080/backGetAll")
            .then(res => {
                this.setState({
                    product: res.data

                })
                console.log(res.data);

            }).catch(err => {
                console.log(err);

            })
    }
    url(a){

    var d="http://localhost:8080/image/"+a+".jpg"
    return d

    }
    componentDidMount() {
        this.getAll()
    }
    render() {
        return (
            <div>



                    {this.state.product.length > 0 ? <div className="row">
                        {this.state.product.map((prod) => {
                            var d= "http://localhost:8080/image/"+prod.img1+".jpg"
                            return (
                                <div className="col-md-5 col-sm-12 col-xs-12 card " class="d">

                                    <img class="card-img-top" src="23.jpg" alt="Card image cap" />
                                    <div class="card-body">
                                        <h2 class="card-title">{prod.titre}</h2>
                                        <p class="card-text">{prod.description}</p>
                                    </div>
                                    <ul class="list-group list-group-flush">
                                        <li class="list-group-item"><h3>{prod.prix} $</h3></li>

                                    </ul>
                                    <div class="card-body">
                                        <a href="#" class="card-link">Card link</a>
                                        <a href="#" class="card-link">Another link</a>
                                    </div>
                                </div>
                            )
                        })}
                    </div> : ""}





            </div>

        )
    }
}
