import React, { Component } from 'react'
import axios from 'axios'
import { Redirect } from "react-router-dom";
import "./home.css"

export default class home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            product: ""
        }
    }


    getAll() {
        axios.get("http://localhost:8080/backGetAll")
            .then(res => {
                this.setState({
                    product: res.data
                })
                console.log(res.data);

            }).catch(err => {
                console.log(err);

            })
    }
    componentDidMount() {
        this.getAll()
    }
    render() {
        return (
            <div>
                <div className="row">


                    {this.state.product.length > 0 ? <div>
                        {this.state.product.map((prod) => {
                            return (
                                <div className="col-md-4 col-sm-12 col-xs-12 card" id="r">
                                    <img class="card-img-top" src="23.jpg" alt="Card image cap" />
                                    <div class="card-body">
                                        <h2 class="card-title">{prod.titre}</h2>
                                        <p class="card-text">{prod.description}</p>
                                    </div>
                                    <ul class="list-group list-group-flush">
                                        <li class="list-group-item">Cras justo odio</li>
                                        <li class="list-group-item">Dapibus ac facilisis in</li>
                                        <li class="list-group-item">Vestibulum at eros</li>
                                    </ul>
                                    <div class="card-body">
                                        <a href="#" class="card-link">Card link</a>
                                        <a href="#" class="card-link">Another link</a>
                                    </div>
                                </div>
                            )
                        })}
                    </div> : ""}





                </div>
            </div>

        )
    }
}
