import React, {Component} from 'react';
import {confirmAlert} from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';


class Tableau extends Component {

  constructor() {
		super();
		this.state = {
			Prix: ''
		}
  }
  onChange (event) {
    this.setState({
      value: event.target
    })
  }
  handleFullNameChange = (e) => {
		this.setState({
			Prix: e.target.value
		})
	}
  //  EditUserForm = props => {
  //    const [ user, setUser ] = useState(this.props.currentUser);

  //    const handleInputChange = event => {
  //      const { name, value } = event.target;

  //      setUser({ ...user, [name]: value });
  //  };


  submit = (e,a) => {
    confirmAlert({
      title: '',
      message:"Suppression Produit" ,
      buttons: [
        {
          label: 'OUI',
          onClick: () => this.props.suppr(e)
        },
        {
          label: 'NON',
          onClick: () => console.log('suppression refusée')
        }
      ],

    })
  }

  submit2 = (a) => {
    confirmAlert({
      customUI: ({ onClose }) => {
        return (
          <div className='custom-ui '>
              <form


              >
                  <input type="Text" value={this.state.value} onChange={this.onChange}></input>
                    <br/>
                    <button onClick={()=>{
                      this.props.edit(a,this.state.value)
                       ;onClose()} 
                    }>

                      OK
                    </button>
                    <span>     </span>
                    <button onClick={() => {
                        onClose()
                    }}>Annuler</button>
              </form>

          </div>
        )
      }
    })
  }

  render() {
    return(
      <div>
      <table className="table table-hover table-bordered">
      <thead>
        <tr className="table-primary">
          <th scope="col" className="act">id</th>
          <th scope="col">Produit</th>
          <th scope="col">Prix</th>
          <th scope="col" className="act">Actions</th>
        </tr>
      </thead>
      <tbody>
        {this.props.users.length > 0 ? (
          this.props.users.map(user => (
            <tr>
              <td>{user.id}</td>
              <td>{user.Produits.toUpperCase()}</td>
              <td>{user.Prix}</td>
              <td>
              <label>
          <button aria-label="Supprimer" id="suppr"
            onClick={() => this.submit(user.id,user.Produits)} className ="btn btn-danger" >X</button>


            <button className ="btn btn-success" id='edit'
            onClick={() => this.submit2(user.id)}>
              Edit
            </button>

            </label>
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan={4}>Aucun enregistrement</td>
          </tr>
        )}
      </tbody>
      </table>
      <button id="total" className="btn btn-info">TOTAL</button>
      <div id="total1">TOTAL =</div>
  </div> 
      )
      }
  }



export default Tableau;
