import React, { Component } from 'react'
import { connect } from 'react-redux'

class panier extends Component {
    componentDidMount() {
        console.log("props panier", this.props.panier);

    }
    render() {
        return (
            <div>
                <div class="container">
                    <h2>Liste des achats que vous avez effectuer</h2>

                    <table class="table table-dark">
                        <thead>
                            <tr>
                                <th>Produit</th>
                                <th>Titre</th>
                                <th>Prix</th>
                            </tr>
                        </thead>
                            <tbody>

                                {this.props.panier.length > 0 ? this.props.panier.map((pan) => {
                                    var d = "http://localhost:8080/image/" + pan.img1
                                    return <tr>
                                        <td><img src={d} width="50px" /></td>
                                        <td>Doe</td>
                                        <td>john@example.com</td>
                                    </tr>

                                }) : ""}


                            </tbody>
                    </table>
                </div>
                </div>
                )
            }
        }
const mapStateToProps = (state) => {
    return {
                    panier: state.panier
            }
        }
        export default connect(mapStateToProps)(panier)
