import React, { Component } from 'react'
import { MDBContainer, MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavbarToggler, MDBCollapse, MDBNavItem, MDBNavLink, MDBIcon } from 'mdbreact';
import { BrowserRouter as Router } from 'react-router-dom';
import axios from 'axios'
import { MDBRow, MDBCol, MDBCard, MDBCardBody, MDBMask, MDBView, MDBBtn } from "mdbreact";

export default class Particulier extends Component {
    constructor(props) {
        super(props);
        this.state = {
            collapse: false,
            atelier: ""
        };
        this.onClick = this.onClick.bind(this);
    }

    onClick() {
        this.setState({
            collapse: !this.state.collapse,
        });
    }
    componentDidMount() {
        this.getAtelier()
    }

    getAtelier() {
        axios.get("http://localhost:8080/getAtelier")
            .then(resp => {
                this.setState({ atelier: resp.data })
                console.log(resp);

            }).catch(err => {
                console.log(err);

            })
    }

    render() {
        const bgPink = { backgroundColor: '#e91e63' }
        const container = { height: 1300 }
        return (
            <div>
                <Router>
                    <header>
                        <MDBNavbar style={bgPink} color="#f3671f" dark expand="md" scrolling fixed="top">
                            <MDBNavbarBrand href="/">
                                <strong>Navbar</strong>
                            </MDBNavbarBrand>
                            <MDBNavbarToggler onClick={this.onClick} />
                            <MDBCollapse isOpen={this.state.collapse} navbar>
                                <MDBNavbarNav left>
                                    <MDBNavItem active>
                                        <MDBNavLink to="/" exact >Home</MDBNavLink>
                                    </MDBNavItem>
                                </MDBNavbarNav>
                                <MDBNavbarNav right>
                                    <MDBNavItem>
                                        <MDBNavLink to="/login">Login</MDBNavLink>
                                    </MDBNavItem>
                                    <MDBNavItem>
                                        <MDBNavLink to="#"></MDBNavLink>
                                    </MDBNavItem>
                                    <MDBNavItem>
                                        <MDBNavLink to="#"></MDBNavLink>
                                    </MDBNavItem>
                                </MDBNavbarNav>
                            </MDBCollapse>
                        </MDBNavbar>
                    </header>
                </Router>
                <MDBContainer style={container} className="text-center mt-5 pt-5">
                    <MDBCard className="my-5 px-5 pb-5">
                        <MDBCardBody>
                            <h2 className="h1-responsive font-weight-bold text-center my-5">
                                Recent posts
                             </h2>
                            <p className="text-center w-responsive mx-auto mb-5">
                                Duis aute irure dolor in reprehenderit in voluptate velit esse
                                cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                                cupidatat non proident, sunt in culpa qui officia deserunt mollit
                                anim id est laborum.
                             </p>


                            {this.state.atelier.length > 0 ? this.state.atelier.map((ate, index) => {
                                return <div>
                                    <MDBRow>

                                        <MDBCol lg="5">
                                            <MDBView className="rounded z-depth-2 mb-lg-0 mb-4" hover waves>
                                                <img
                                                    className="img-fluid"
                                                    src="https://mdbootstrap.com/img/Photos/Others/img%20(27).jpg"
                                                    alt=""
                                                />
                                                <h2 href="#!">

                                                  <MDBMask overlay="white-slight" /> 
                                                </h2>
                                            </MDBView>
                                        </MDBCol>
                                        <MDBCol lg="7">
                                            <a href="#!" className="green-text">
                                                <h3 className="font-weight-bold mb-3">
                                                <strong> 
                                                    {ate.titre}
                                                    </strong>
                                                 </h3>
                                            </a>
                                            <p>
                                                {ate.description}
                                            </p>
                                            <p className="font-weight-bold mb-3 p-0">
                                                Date:{ate.date} |

                                            Heure du debut:{ate.heureDebut} |

                                            Durée:{ate.duree} |

                                            Place :{ate.placeDispo} |

                                            place Reservé:{ate.placeReserve} |

                                            Prix:{ate.prix} $ 
                                            </p>


                                            <MDBBtn color="success" size="md" className="waves-light " onClick={(e)=>{
                                                e.preventDefault()
                                                this.inscr()
                                            }}>
                                                S' inscrire
                                             </MDBBtn>
                                        </MDBCol>
                                    </MDBRow>
                                    <hr className="my-5" />
                                </div>


                            }) : ""}





                        </MDBCardBody>
                    </MDBCard>
                </MDBContainer>
            </div>
        );
    }
}
