import React, { Component } from 'react'
import { connect } from 'react-redux'

class panier extends Component {

    constructor(props) {
        super(props);

        this.state = {
            total:""

        }


    }

    componentDidMount() {
        console.log("props panier", this.props.panier);

    }

    totale(){
        let a=0
        for(i=0;i<this.props.panier.length;i++){
            a=a+parseInt(this.props.panier[i].prix)
        }
    }
    render() {
        return (
            <div>
                <div class="container">
                    <h2>Liste des achats que vous avez effectuer</h2>

                    <div class="container">

                        <table class="table table-dark">
                            <thead>
                                <tr>
                                    <th>Firstname</th>
                                    <th>Lastname</th>
                                    <th>Email</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.props.panier.length > 0 ? this.props.panier.map((pan) => {
                                    var d = "http://localhost:8080/image/" + pan.img1
                                    return <tr>
                                        <td><img src={d} width="50px"/></td>
                                        <td>{pan.titre}</td>
                                        <td>{pan.prix} $</td>
                                    </tr>

                                }) : ""}

                            </tbody>
                        </table>
                        <div>TOTAL A PAYER: {this.state.total}</div>
                    </div>
                </div>
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        panier: state.panier
    }
}
export default connect(mapStateToProps)(panier)
