import React, { Component } from 'react'

export default class uploadProduct extends Component {



    constructor(props) {
        super(props);

        this.state = {
            email: '',
            passWord: "",
            redirect: "",
            nom: ""

        }
        this.change = this.change.bind(this)

    }
    componentDidMount() {
        this.setRedirect()
    }

    setRedirect = () => {
        console.log("localstor", localStorage.getItem('token'));

        this.setState({
            redirect: localStorage.getItem('token')
        })
    }

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to='/home' />
        }
    }




    setAuthToken(token) {
        if (token) {
            console.log("if token");

            var t = "bearer " + token
            console.log(t);

            axios.defaults.headers.common['authorization'] = t
        } else {
            console.log("else token");
            delete axios.defaults.headers.common['authorization'];


        }
    }

    change = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    login = (e) => {
        axios.post('http://localhost:8080/backRegister', e)
            .then(result => {
                console.log("result", result);
                var token = result.data.token
                localStorage.setItem('token', token)
                this.setRedirect()
                // this.setAuthToken(token)

            }
            )
            .catch(result => {
                console.log("erreur", result);

            });
    }




    handleUploadImage(ev) {
        ev.preventDefault();

        const data = new FormData();
        data.append('file', this.uploadInput.files[0]);
        data.append('nom', this.state.nom);
        data.append('titre', this.state.titre)
        data.append('article', this.state.article)

        fetch('http://localhost:8080/article', {
            method: 'POST',
            body: data,
        }).then((response) => {
            response.json().then((body) => {
                this.setState({ imageURL: `http://localhost:8080/public/${body.file}` });
                console.log('nom', this.state.nom);
            });
        });
    }







    render() {
        return (
            <div>
                <div id="login">
                    <h3 class="text-center text-white pt-5">Login form</h3>
                    <div class="container">
                        <div id="login-row" class="row justify-content-center align-items-center">
                            <div id="login-column" class="col-md-6">
                                <div id="login-box" class="col-md-12">
                                    <form id="login-form" class="form" action="" method="post">
                                        <h3 class="text-center text-info">Login</h3>
                                        <div class="form-group">
                                            <label for="username" class="text-info">Username:</label><br />

                                                {/* input titre */}
                                            <input type="text" name="titre" value={this.state.titre} onChange={this.change} id="username" class="form-control" />
                                        </div>
                                        <div class="form-group">
                                            <label for="password" class="text-info">Password:</label><br />
                                                {/* input description */}

                                            <input type="text" name="description" value={this.state.description} onChange={this.change} id="password" class="form-control" />
                                        </div>

                                        <div class="form-group">
                                            <label for="password" class="text-info">Password:</label><br />
                                                {/* input prix */}

                                            <input type="text" name="prix" value={this.state.prix} onChange={this.change} id="password" class="form-control" />
                                        </div>

                                        <div class="input-group">
                                            <div class="input-group-prepend">
                                                <span class="input-group-text" id="inputGroupFileAddon01">Upload</span>
                                            </div>
                                            <div class="custom-file">


                                                {/* input file */}
                                                <input type="file" class="custom-file-input" id="inputGroupFile01"
                                                    aria-describedby="inputGroupFileAddon01"  name="file0"/>
                                                <label class="custom-file-label" for="inputGroupFile01">Choose file</label>
                                            </div>
                                        </div>




                                        <div class="form-group">

                                            <input type="submit" name="submit" class="btn btn-info btn-md" value="submit"
                                                onClick={
                                                    (e) => {
                                                        e.preventDefault()
                                                        this.login({
                                                            email: this.state.email,
                                                            passWord: this.state.passWord
                                                        })
                                                    }
                                                } />


                                        </div>
                                        <div id="register-link" class="text-right">
                                            <a href="#" class="text-info">Register here</a>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}





<input ref={(ref) => { this.uploadInput = ref; }} type="file" class="form-control" id="inputCity" />
