import React, { Component } from 'react'
import axios from 'axios'
import {Redirect} from "react-router-dom";

class get extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: '',
            passWord: "",
            redirect: ""

        }
        this.change = this.change.bind(this)

    }
    componentDidMount(){
        this.setRedirect()
    }

    setRedirect = () => {
        console.log("localstor",localStorage.getItem('token'));

        this.setState({
            redirect: localStorage.getItem('token')
        })
    }

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to='/home' />
        }
    }




    setAuthToken(token) {
        if (token) {
            console.log("if token");

            var t = "bearer " + token
            console.log(t);

            axios.defaults.headers.common['authorization'] = t
        } else {
            console.log("else token");
            delete axios.defaults.headers.common['authorization'];


        }
    }

    change = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    login = (e) => {
        axios.post('http://localhost:8080/backLogin', e)
            .then(result => {
                console.log("result", result);
                var token = result.data.token
                localStorage.setItem('token', token)
                this.setRedirect()
                // this.setAuthToken(token)
                this.props.history.push('/Home')

            }
            )
            .catch(result => {
                console.log("erreur", result);

            });
    }

    render() {
        return (
            <div>
                <form>
                    {/* {this.renderRedirect()} */}
                    <div>
                        <input name="email" value={this.state.email} onChange={this.change} className="email" />
                    </div>
                    <div>
                        <input name="passWord" value={this.state.passWord} onChange={this.change} className="passWord" />
                    </div>
                    <button onClick={
                        (e) => {
                            e.preventDefault()
                            this.login({
                                email: this.state.email,
                                passWord: this.state.passWord
                            })
                        }
                    }>HAHAHAs</button>
                </form>

            </div>
        )
    }
}
export default get;
