import React, { Component } from 'react'
import { connect } from 'react-redux'
import paypal from 'paypal-rest-sdk';

class panier extends Component {

    constructor(props) {
        super(props);

        this.state = {
            total: ""

        }


    }
    create_payment_json = {
        "intent": "sale",
        "payer": {
            "payment_method": "paypal"
        },
        "redirect_urls": {
            "return_url": "http://localhost:3000/",
            "cancel_url": "http://localhost:3000/cancel"
        },
        "transactions": [{
            "item_list": {
                "items": [{
                    "name": "Red Sox Hat",
                    "sku": "001",
                    "price": "25.00",
                    "currency": "USD",
                    "quantity": 1
                }]
            },
            "amount": {
                "currency": "USD",
                "total": "25.00"
            },
            "description": "Hat for the best team ever"
        }]
    };



    paypalCheckOut() {
        paypal.payment.create(create_payment_json, function (error, payment) {
            if (error) {
                throw error;
            } else {
                for (let i = 0; i < payment.links.length; i++) {
                    if (payment.links[i].rel === 'approval_url') {
                        res.redirect(payment.links[i].href);
                    }
                }
            }
        });


}






componentDidMount() {

    paypal.configure({
        'mode': 'sandbox', //sandbox or live
        'client_id': 'AaU8tQfmz1_MFDTKuf84yYERXvdDt2ZFJVrxhNW_49DazF4A_F0VBuKyV5_nntyEdZqUa5Oq9ZBj65GV',
        'client_secret': 'EAZ8aFDU4lHHLy1bQqULYWqznf3dBknXZW3AH__zFC0bUs8AGUyR6RNbm-jHvqtikX7PsSqMO5vxuvKm'
    });


    console.log("props panier", this.props.panier);
    this.totale()

}

totale() {
    let a = 0
    for (let i = 0; i < this.props.panier.length; i++) {
        a = a + parseInt(this.props.panier[i].prix)
    }
    this.setState({
        total: a
    })
}

supprimer(e) {
    const action = { type: "SUPPRPANIER", value: e }
    this.props.dispatch(action)
    this.totale()
}
render() {
    return (
        <div>
            <div class="container">
                <h2>Liste des achats que vous avez effectuer</h2>

                <div class="container">

                    <table class="table table-dark">
                        <thead>
                            <tr>
                                <th>Photo</th>
                                <th>Produit</th>
                                <th>Prix</th>
                                <th>Supprimer</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.props.panier.length > 0 ? this.props.panier.map((pan, index) => {
                                var d = "http://localhost:8080/image/" + pan.img1


                                return <tr>
                                    <td><img src={d} width="50px" /></td>
                                    <td>{pan.titre}</td>
                                    <td>{pan.prix} $</td>
                                    <td><button className="btn btn-danger" onClick={() => {
                                        this.supprimer(index)
                                    }}>X</button></td>
                                </tr>

                            }) : ""}

                        </tbody>
                    </table>
                    <div>TOTAL A PAYER: {this.state.total} $</div>
                    <button class="btn btn-primary" >Payer Paypal</button>
                </div>
            </div>
        </div>
    )
}
}
const mapStateToProps = (state) => {
    return {
        panier: state.panier
    }
}
export default connect(mapStateToProps)(panier)
