import React, { Component } from 'react'
import { Provider } from 'react-redux'
import Store from './Store/store'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Login from './Component/login'
import Home from './Component/home'
import Register from './Component/register'
import { connect } from 'react-redux'
import Dashboard from './Component/dashboard/dashboard'
import 'bootstrap/dist/css/bootstrap.min.css';
import Nav from './Component/navbar'



// import Login from "./login"
// import Register from "./register"
// import Home from "./home"
import Dash from "./Component/dashboard/dashboard"

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      redirect: ""
    }
  }
  componentDidMount() {
    console.log("avant",this.props.connecter);
        this._etatConnexion(localStorage.getItem("token"))
        console.log("apres",this.props.connecter);
  }
  _etatConnexion(e) {
    const action = { type: "CONNECTER", value: e }
    this.props.dispatch(action)
}

  render() {
    return (
      <div className="app">

        <Provider store={Store}>

          <Router>
            <div>

              <nav class="navbar navbar-expand-lg navbar-light bg-light">
                <a class="navbar-brand" href="#">Navbar</a>
                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                  <span class="navbar-toggler-icon"></span>
                </button>
                <div class="collapse navbar-collapse" id="navbarNav">
                  <ul class="navbar-nav">
                    <li class="nav-item active">
                      <Link class="nav-link" to="/" >Home <span class="sr-only">(current)</span></Link>
                    </li>
                    <li class="nav-item">
                      <Link class="nav-link" to="/login">Sign in</Link>
                    </li>
                    <li class="nav-item">
                      <Link class="nav-link" to="/register">Sign up</Link>
                    </li>
                    <li class="nav-item">
                      <Link class="nav-link disabled" to="#">Panier</Link>
                    </li>
                  </ul>
                </div>


              </nav>
              <Route path="/Dashboard" component={Dash} />
              <Route path="/" exact component={Home} />
              <Route path="/login" component={Login} />
              <Route path="/register" component={Register} />

            </div>

          </Router>
        </Provider>
      </div>
    )
  }
}


