import React, { Component } from 'react'
import axios from 'axios'
import { MDBContainer,MDBRow, MDBCol, MDBCard, MDBCardBody, MDBMask, MDBView, MDBBtn } from "mdbreact";

import { connect } from 'react-redux'

class listeAtelier extends Component {


    constructor(props) {
        super(props);
        this.state = {
            collapse: false,
            atelier: ""
        };
        this.onClick = this.onClick.bind(this);
    }

    onClick() {
        this.setState({
            collapse: !this.state.collapse,
        });
    }
    componentDidMount() { 
        this.getAtelier()
    }

    getAtelier() {
        axios.get("http://localhost:8080/getAtelier")
            .then(resp => {
                this.setState({ atelier: resp.data })
                console.log(resp);

            }).catch(err => {
                console.log(err);

            })
    }
    inscr(e) {

        const action = { type: "idAt", value: e }
        this.props.dispatch(action)
        this.props.history.push("/inscription")
    }




    render() {

        const container = { height: 1300 }
        return (
            <div>
                <MDBContainer style={container} className="text-center mt-5 pt-5">
                    <MDBCard className="my-5 px-5 pb-5">
                        <MDBCardBody>
                            <h1 className="h1-responsive font-weight-bold text-center my-5" style={{color:"#d05c62 ", fontFamily:"roboto !important"}}>
                            Liste des ateliers
                             </h1>
                            <h2 className="text-center w-responsive mx-auto mb-5" style={{fontFamily:"roboto !important"}}>
                            Liste de tous les ateliers ou vous pouvez vous inscrire
                             </h2>


                            {this.state.atelier.length > 0 ? this.state.atelier.map((ate, index) => {
                                var d= "http://localhost:8080/image/"+ate.image
                                return <div>
                                     {ate.visibilite?<div>
                                        <MDBRow>

                               <MDBCol lg="5">
                                   <MDBView className="rounded z-depth-2 mb-lg-0 mb-4" hover waves>
                                       <img
                                           className="img-fluid"
                                           src={d}
                                           alt=""
                                           width="200em"
                                       />
                                       <h2 href="#!">

                                         <MDBMask overlay="white-slight" /> 
                                       </h2>
                                   </MDBView>
                               </MDBCol>
                               <MDBCol lg="7">
                                   <a href="#!" className="green-text">
                                       <h3 className="font-weight-bold mb-3" style={{color:"#f3671f"}}>
                                       <strong> 
                                           {ate.titre}
                                           </strong>
                                        </h3>
                                   </a>
                                   <p>
                                       {ate.description}
                                   </p>
                                   <p className="font-weight-bold mb-3 p-0">
                                       Date:{ate.date} |

                                   Heure du debut:{ate.heureDebut} |

                                   Durée:{ate.duree} |

                                   Place :{ate.placeDispo} |

                                   place Reservé:{ate.placeReserve} |

                                   Prix:{ate.prix} $ 
                                   </p>


                                   <MDBBtn color="success" size="md" className="waves-light " onClick={(e)=>{
                                       e.preventDefault()
                                       this.inscr(ate._id)

                                   }}>
                                       S' inscrire
                                    </MDBBtn>
                               </MDBCol>
                           </MDBRow>
                           <hr className="my-5" />
                                     </div>:""}

                                </div>


                            }) : ""}





                        </MDBCardBody>
                    </MDBCard>
                </MDBContainer>
            </div>
        )
    }
}


const mapStateToProps = (state) => {
    return {
        idAtelier: state.idAtelier
    }
}
export default connect(mapStateToProps)(listeAtelier)
