import React, { Component } from 'react'
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from "mdbreact";
import axios from 'axios'
import { connect } from 'react-redux'

class inscription extends Component {
    state = {
        nom: "",
        prenom: "",
        email: "",
        tel: "",

    };
    componentDidMount(){
        console.log(this.props.idAtelier);

    }

    submitHandler = event => {
        event.preventDefault();
        event.target.className += " was-validated";
        this.inscription({
            nom:this.state.nom,
            prenom:this.state.prenom,
            email:this.state.email,
            tel:this.state.tel,
            idAtelier:this.props.idAtelier
        })
    };

    changeHandler = event => {
        this.setState({ [event.target.name]: event.target.value });
    };
    inscription(r){
        console.log(r);

        axios.post("http://localhost:8080/inscription",r)
        .then(res=>{
            axios.put("http://localhost:8080/inscription",{_id:this.props.idAtelier})
            console.log(res);

        }).catch(err=>{
            console.log(err);

        })
    }

    render() {
        const container = { height: 1300 }
        return (
            <MDBContainer style={container} className="text-center mt-5 pt-5">
                <form
                    className="needs-validation"
                    noValidate
                    onSubmit={this.submitHandler}

                >
                    <MDBRow>
                        <MDBCol md="4" className="mb-3">
                            <label
                                htmlFor="defaultFormRegisterNameEx"
                                className="grey-text"
                            >
                                Nom
                            </label>
                            <input
                                value={this.state.fname}
                                name="nom"
                                onChange={this.changeHandler}
                                type="text"
                                id="defaultFormRegisterNameEx"
                                className="form-control"
                                placeholder="First name"
                                required
                            />
                            <div className="valid-feedback">Looks good!</div>
                        </MDBCol>
                        <MDBCol md="4" className="mb-3">
                            <label
                                htmlFor="defaultFormRegisterEmailEx2"
                                className="grey-text"
                            >
                                Prenom
                             </label>
                            <input
                                value={this.state.lname}
                                name="prenom"
                                onChange={this.changeHandler}
                                type="text"
                                id="defaultFormRegisterEmailEx2"
                                className="form-control"
                                placeholder="Last name"
                                required
                            />
                            <div className="valid-feedback">Looks good!</div>
                        </MDBCol>
                        <MDBCol md="4" className="mb-3">
                            <label
                                htmlFor="defaultFormRegisterConfirmEx3"
                                className="grey-text"
                            >
                                Email
                             </label>
                            <input
                                value={this.state.email}
                                onChange={this.changeHandler}
                                type="email"
                                id="defaultFormRegisterConfirmEx3"
                                className="form-control"
                                name="email"
                                placeholder="Your Email address"
                                required
                            />
                            <small id="emailHelp" className="form-text text-muted">
                                We'll never share your email with anyone else.
                            </small>
                        </MDBCol>
                    </MDBRow>
                    <MDBRow>
                        <MDBCol md="4" className="mb-3">
                            <label
                                htmlFor="defaultFormRegisterPasswordEx4"
                                className="grey-text"
                            >
                                Telephone
                            </label>
                            <input
                                value={this.state.city}
                                onChange={this.changeHandler}
                                type="text"
                                id="defaultFormRegisterPasswordEx4"
                                className="form-control"
                                name="tel"
                                placeholder="City"

                            />
                            <div className="invalid-feedback">
                                Please provide a valid city.
                            </div>
                            <div className="valid-feedback">Looks good!</div>
                        </MDBCol>

                    </MDBRow>
                    {/* <MDBCol md="4" className="mb-3">
                        <div className="custom-control custom-checkbox pl-3">
                            <input
                                className="custom-control-input"
                                type="checkbox"
                                value=""
                                id="invalidCheck"
                                required
                            />
                            <label className="custom-control-label" htmlFor="invalidCheck">
                                Agree to terms and conditions
                            </label>
                            <div className="invalid-feedback">
                                You must agree before submitting.
                            </div>
                        </div>
                    </MDBCol> */}
                    <MDBBtn color="primary" type="submit" >
                        S' inscrire
              </MDBBtn>

                </form>
            </MDBContainer>
        );
    }
}


const mapStateToProps = (state) => {
    return {
        idAtelier: state.idAtelier
    }
}
export default connect(mapStateToProps)(inscription)
