import React, { useState } from 'react'

const Form = props => {
   const initial = { id: null, Produits: '', Prix: '' }
   const [ user, setUser ] = useState(initial)
   const changed = event => {
   const { name, value } = event.target
   setUser({ ...user, [name]: value })
 }
	return (
		<form 
      onSubmit={event => {
         event.preventDefault()

         if (!user.Produits || !user.Prix) return

         if(isNaN(user.Prix)){
            var erreur = document.getElementById('erreur')
            erreur.innerHTML = 'Entrer un nombre'

        }else{
            var erreur = document.getElementById('erreur')
            erreur.innerHTML = ''
            props.updateUser(user.id, user)
            props.ajouter(user)
            props.setCount(props.count + 1)
            setUser(initial)
        }


       }}>
			<label>Produit</label>
			<input type="text" name="Produits" value={user.Produits} onChange={changed}/>
			<label>Prix</label>
			<input type="text" name="Prix"  value={user.Prix} onChange={changed} />
         <label id = 'ariary'>Ar</label>
			<button id="ajout" className="btn btn-primary">Ajouter</button>
         <p id="erreur"></p>

		</form>
   )
}
export default Form
