import React, { Component } from 'react'
import axios from 'axios'

class get extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: '',
            passWord: ""

        }
        this.change=this.change.bind(this)

    }



    componentDidMount() {

    }
    change = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    login = (e) => {
        axios.post('http://localhost:8080/backLogin', e)
            .then(result => {
                console.log("result", result);
                var token = result.data.token
                localStorage.setItem('token',token)

                }
            )
            .catch(result => {
                console.log("erreur", result);

            });
    }

    render() {
        return (
            <div>
                <form>
                    <div>
                        <input name="email" value={this.state.email} onChange={this.change} className="email" />
                    </div>
                    <div>
                        <input name="passWord" value={this.state.passWord} onChange={this.change} className="passWord" />
                    </div>
                    <button onClick={
                        (e) => {
                            e.preventDefault()
                            this.login({
                                email: this.state.email,
                                passWord: this.state.passWord
                            })
                        }
                    }>HAHAHAs</button>
                </form>

            </div>
        )
    }
}
export default get;
