import React, { useState } from 'react';
import Tableau from './Tableau';
import Form from './Form';
import './Form.css';
import './Tableau.css';
// import { log } from 'util';

const App = () => {
    const usersData = []

		const [ users, setUsers ] = useState(usersData)
    const [count, setCount] = useState(0);



    const edit =(id,a)  => {
     // user.id==id?user.Prix=a:console.log(user.id);
     setUsers(  users.map(user => (user.id === id ? user.Prix : user.Prix=a)))

      console.log(id);
      console.log(a);

    }

    const ajouter = user => {
      user.id = count
      user.Produits= user.Produits.substr(0,1).toUpperCase()+	user.Produits.substr(1,user.Produits.length).toLowerCase()
      setUsers([ ...users, user ])

    }
    const suppr = id => {
        setUsers(users.filter(user => user.id !== id))
    }

    return (
      <div className="container bg-danger">
      <div className="flex-row">
        <div className="flex-large">
          <Form ajouter={ajouter} setCount={setCount} count={count}/>
        </div>
        <div className="flex-large">
          <Tableau users={users} suppr={suppr} edit={edit}/>
        </div>
      </div>
    </div>
    )
  };

export default App;
