import React, { Component } from 'react'
import axios from 'axios'
import { Redirect } from "react-router-dom";
import "./home.css"

export default class home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            product: "",
            url: ""
        }
    }


    getAll() {
        axios.get("http://localhost:8080/backGetAll")
            .then(res => {
                this.setState({
                    product: res.data

                })
                console.log(res.data);

            }).catch(err => {
                console.log(err);

            })
    }
    url(a) {

        var d = "http://localhost:8080/image/" + a + ".jpg"
        return d

    }
    componentDidMount() {
        this.getAll()
    }
    render() {
        return (
            <div>
                <nav class="navbar navbar-expand-lg navbar-dark primary-color">
                    <a class="navbar-brand" href="#">Navbar</a>
                    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav"
                        aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                        <span class="navbar-toggler-icon"></span>
                    </button>
                    <div class="collapse navbar-collapse" id="navbarNav">
                        <ul class="navbar-nav">
                            <li class="nav-item active">
                                <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#">Features</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#">Pricing</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link disabled" href="#">Disabled</a>
                            </li>
                        </ul>
                    </div>
                </nav>


                {this.state.product.length > 0 ? <div className="row">
                    {this.state.product.map((prod) => {
                        var d = "http://localhost:8080/image/" + prod.img1
                        return (
                            <div class="card" class="r">
                                <img class="card-img-top" src={d} alt="Card image cap" />
                                <div class="card-body">
                                    <h2 class="card-title">{prod.titre}</h2>
                                    <p class="card-text">{prod.description}</p>
                                    <p class="card-text">{prod.prix} $</p>
                                    <a href="#" class="btn btn-primary">Ajouter panier</a>
                                </div>
                            </div>
                        )
                    })}
                </div> : ""}





            </div>

        )
    }
}
