import React, { Component } from 'react'
import axios from 'axios'
import { MDBContainer,MDBRow, MDBCol, MDBCard, MDBCardBody, MDBMask, MDBView, MDBBtn } from "mdbreact";
import { connect } from 'react-redux'

class atelierCuisinier extends Component {
    constructor(props) {
        super(props);
        this.state = {
            collapse: false,
            atelier: ""
        };
        this.onClick = this.onClick.bind(this);
    }
    onClick() {
        this.setState({
            collapse: !this.state.collapse,
        });
    }

    componentDidMount(){

        this.getAtelier()
    }


    // localStorage.getItem("id")
    getAtelier() {
        axios.get("http://localhost:8080/getAteliercuis/"+localStorage.getItem("id"),this.setAuthToken(localStorage.getItem('token')))
            .then(resp => {
                this.setState({ atelier: resp.data.resp })
                console.log(resp);

            }).catch(err => {
                console.log(err);

            })
    }
    setAuthToken(token) {
        if (token) {
            console.log("if token");

            var t = "bearer " + token
            console.log(t);

            axios.defaults.headers.common['authorization'] = t
        } else {
            console.log("else token");
            delete axios.defaults.headers.common['authorization'];


        }
    }
    desactivation(a){
        axios.put("http://localhost:8080/updateVisibilite",{id:a},this.setAuthToken(localStorage.getItem('token')))
        .then(resp=>{
            this.getAtelier()
            console.log("update visibilite",resp.data);

        }).catch(err=>{
            console.log("err visibilite",err);

        })
    }

    modifier(e){
        const action = { type: 'mod', value: e }
        this.props.dispatch(action)
        this.props.history.push("/modification")
    }
    render() {

        const container = { height: 1300 }
        return (
            <div>
                 <div id="demo" class="carousel slide" data-ride="carousel">


<ul class="carousel-indicators">
    <li data-target="#demo" data-slide-to="0" class="active"></li>
    <li data-target="#demo" data-slide-to="1"></li>
    <li data-target="#demo" data-slide-to="2"></li>
</ul>


<div class="carousel-inner">
<div class="carousel-item">
        <div class="carousel-caption">
        <h3 style={{fontSize:"2.5em",color:"black"}}>Nos activités</h3>
            <p style={{fontSize:"2em",color:"black"}}>Nous sommes un centre de formation de cuisine qui propose des ateliers à nos élèves à
                partir de 12 ans, mais aussi à des particuliers.
                </p>
        </div>
        <img src="./cuij2.jpg" alt="New York" style={{ width: "100%" }} />
    </div>
    <div class="carousel-item active">
        <div class="carousel-caption">
        <h3 style={{fontSize:"2.5em",color:"black"}}>Nos activités</h3>
            <p style={{fontSize:"2em",color:"#f3671f"}}>Les cours proposés aux particuliers permettent de financer l’achat de matériels et de
                matières premières.</p>
        </div>
        <img src="./cuisij.png" alt="cuis1" style={{ width: "100%" }} />
    </div>


    <div class="carousel-item">
        <div class="carousel-caption">
            <h3 style={{fontSize:"2.5em",color:"black"}}>Cibles</h3>
            <p style={{fontSize:"2em",color:"black"}}>Nos cibles sont les jeunes actifs entre 25 - 35 ans. Des personnes qui veulent apprendre à
            cuisiner afin de manger correctement.</p>
        </div>
        <img src="./cuij2.jpg" alt="Chicago" style={{ width: "100%" }} />
    </div>

</div>


<a class="carousel-control-prev" href="#demo" data-slide="prev">
    <span class="carousel-control-prev-icon"></span>
</a>
<a class="carousel-control-next" href="#demo" data-slide="next">
    <span class="carousel-control-next-icon"></span>
</a>

</div>
                <MDBContainer style={container} className="text-center mt-5 pt-5">

                    <MDBCard className="my-5 px-5 pb-5">
                        <MDBCardBody>

                            <h2 className="h1-responsive font-weight-bold text-center my-5" style={{fontFamily:"Roboto"}}>
                                Liste des mes ateliers
                             </h2>
                            <p className="text-center w-responsive mx-auto mb-5">
                                Liste de tous les ateliers que vous avez créé et que vous pouvez modififier ou rendre invisible
                             </p>


                            {this.state.atelier.length > 0 ? this.state.atelier.map((ate, index) => {
                                var d= "http://localhost:8080/image/"+ate.image
                                return <div>
                                    <MDBRow>

                                        <MDBCol lg="5">
                                            <MDBView className="rounded z-depth-2 mb-lg-0 mb-4" hover waves>
                                                <img
                                                    className="img-fluid"
                                                    src={d}
                                                    alt=""

                                                />
                                                <h2 href="#!">

                                                  <MDBMask overlay="white-slight" /> 
                                                </h2>
                                            </MDBView>
                                        </MDBCol>
                                        <MDBCol lg="7">
                                            <a href="#!" className="green-text">
                                                <h3 className="font-weight-bold mb-3"style={{ color:"#f3671f"}}>
                                                <strong> 
                                                    {ate.titre}
                                                    </strong>
                                                 </h3>
                                            </a>
                                            <p>
                                                {ate.description}
                                            </p>
                                            <p className="font-weight-bold mb-3 p-0">
                                                Date:{ate.date} |

                                            Heure du debut:{ate.heureDebut} |

                                            Durée:{ate.duree} |

                                            Place :{ate.placeDispo} |

                                            place Reservé:{ate.placeReserve} |

                                            Prix:{ate.prix} $ 
                                            </p>
                                            {ate.visibilite?<MDBBtn color="primary"   size="md" className="waves-light " onClick={(e)=>{
                                                e.preventDefault()
                                                this.desactivation(ate._id)


                                            }}>
                                                Desactiver
                                             </MDBBtn>:<MDBBtn color="primary"  size="md" className="waves-light " onClick={(e)=>{
                                                e.preventDefault()
                                                this.desactivation(ate._id)


                                            }}>
                                                Activer
                                             </MDBBtn>}


                                             <MDBBtn color="success" size="md" className="waves-light " onClick={(e)=>{
                                                e.preventDefault()
                                                this.modifier(ate)


                                            }}>
                                                Modifier
                                             </MDBBtn>
                                        </MDBCol>
                                    </MDBRow>
                                    <hr className="my-5" />
                                </div>


                            }) : ""}





                        </MDBCardBody>
                    </MDBCard>
                    <footer class="page-footer font-small black" style={{backgroundColor:"#d05c62"}}>


<div class="footer-copyright text-center py-3">© 2018 Copyright:
    <a href="https://mdbootstrap.com/education/bootstrap/" style={{color:"black"}}> RijaPrincy</a>
</div>


</footer>
                </MDBContainer>

            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        modification: state.modification
    }
}
export default connect(mapStateToProps)(atelierCuisinier)
