import React, { Component } from 'react'
import axios from 'axios'
import { MDBContainer,MDBRow, MDBCol, MDBCard, MDBCardBody, MDBMask, MDBView, MDBBtn } from "mdbreact";

export default class atelierCuisinier extends Component {
    constructor(props) {
        super(props);
        this.state = {
            collapse: false,
            atelier: ""
        };
        this.onClick = this.onClick.bind(this);
    }
    onClick() {
        this.setState({
            collapse: !this.state.collapse,
        });
    }

    componentDidMount(){
        this.getAtelier()
    }
    // localStorage.getItem("id")
    getAtelier() {
        axios.get("http://localhost:8080/getAteliercuis/"+localStorage.getItem("id"),this.setAuthToken(localStorage.getItem('token')))
            .then(resp => {
                this.setState({ atelier: resp.data.resp })
                console.log(resp);

            }).catch(err => {
                console.log(err);

            })
    }
    setAuthToken(token) {
        if (token) {
            console.log("if token");

            var t = "bearer " + token
            console.log(t);

            axios.defaults.headers.common['authorization'] = t
        } else {
            console.log("else token");
            delete axios.defaults.headers.common['authorization'];


        }
    }
    render() {

        const container = { height: 1300 }
      var  haha={color:"#d05c62" }
        return (
            <div>
                <MDBContainer style={container} className="text-center mt-5 pt-5">
                    <MDBCard className="my-5 px-5 pb-5">
                        <MDBCardBody>
                            <h2 className="h1-responsive font-weight-bold text-center my-5">
                                Recent posts
                             </h2>
                            <p className="text-center w-responsive mx-auto mb-5">
                                Duis aute irure dolor in reprehenderit in voluptate velit esse
                                cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                                cupidatat non proident, sunt in culpa qui officia deserunt mollit
                                anim id est laborum.
                             </p>


                            {this.state.atelier.length > 0 ? this.state.atelier.map((ate, index) => {
                                var d= "http://localhost:8080/image/"+ate.image
                                return <div>
                                    <MDBRow>

                                        <MDBCol lg="5">
                                            <MDBView className="rounded z-depth-2 mb-lg-0 mb-4" hover waves>
                                                <img
                                                    className="img-fluid"
                                                    src={d}
                                                    alt=""
                                                />
                                                <h2 href="#!">

                                                  <MDBMask overlay="white-slight" /> 
                                                </h2>
                                            </MDBView>
                                        </MDBCol>
                                        <MDBCol lg="7">
                                            <a href="#!" className="green-text">
                                                <h3 className="font-weight-bold mb-3">
                                                <strong> 
                                                    {ate.titre}
                                                    </strong>
                                                 </h3>
                                            </a>
                                            <p>
                                                {ate.description}
                                            </p>
                                            <p className="font-weight-bold mb-3 p-0">
                                                Date:{ate.date} |

                                            Heure du debut:{ate.heureDebut} |

                                            Durée:{ate.duree} |

                                            Place :{ate.placeDispo} |

                                            place Reservé:{ate.placeReserve} |

                                            Prix:{ate.prix} $ 
                                            </p>


                                            <MDBBtn color="primary" style={haha} size="md" className="waves-light " onClick={(e)=>{
                                                e.preventDefault()


                                            }}>
                                                S' inscrire
                                             </MDBBtn>
                                             <MDBBtn color="success" size="md" className="waves-light " onClick={(e)=>{
                                                e.preventDefault()


                                            }}>
                                                Modifier
                                             </MDBBtn>
                                        </MDBCol>
                                    </MDBRow>
                                    <hr className="my-5" />
                                </div>


                            }) : ""}





                        </MDBCardBody>
                    </MDBCard>
                </MDBContainer>
            </div>
        )
    }
}
