import React, { Component } from 'react'
import axios from 'axios'
import {Redirect} from "react-router-dom";

export default class home extends Component {






    constructor(props) {
        super(props);

        this.state = {
            redirect: ""
        }


    }

    setRedirect = () => {
        console.log("localstor", localStorage.getItem('token'));

        this.setState({
            redirect: localStorage.getItem('token')
        })
    }

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to='/' />
        }
    }









    getHome = () => {
        axios.post('http://localhost:8080/backAcc', this.setAuthToken(localStorage.getItem('token')))
            .then(result => {
                console.log("result", result);
            }
            )
            .catch(result => {
                console.log("erreur", result);

            });
    }





    setAuthToken(token) {
        if (token) {
            console.log("if token");

            var t = "bearer " + token
            console.log(t);

            axios.defaults.headers.common['authorization'] = t
        } else {
            console.log("else token");
            delete axios.defaults.headers.common['authorization'];


        }
    }
    componentDidMount() {
        this.getHome()
        // this.setAuthToken(localStorage.getItem('token'))

    }
    render() {
        return (
            <div>
                {this.renderRedirect()}
                welcome
            </div>
        )
    }
}
