import React, { Component } from 'react'
import axios from 'axios'
import { Redirect } from "react-router-dom";
import "./home.css"
import UploadProduct from "./uploadProduct"
import Home from "../home"
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

export default class home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            product: "",
            url: ""
        }
    }


    getAll() {
        axios.get("http://localhost:8080/backGetAll")
            .then(res => {
                this.setState({
                    product: res.data

                })
                console.log(res.data);

            }).catch(err => {
                console.log(err);

            })
    }
    url(a) {

        var d = "http://localhost:8080/image/" + a + ".jpg"
        return d

    }
    componentDidMount() {
        this.getAll()
    }
    render() {
        return (
            <div>
<Router>
                    <div>
                        {this.renderRedirect()}
                        <nav class="navbar navbar-expand-lg navbar-light bg-light">

                            <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                                <span class="navbar-toggler-icon"></span>
                            </button>
                            <div class="collapse navbar-collapse" id="navbarNav">
                                <ul class="navbar-nav">
                                    <li class="nav-item active">
                                        <Link class="nav-link" to="/uploadProduct">upload Product</Link>
                                    </li>
                                    <li class="nav-item">
                                        <Link class="nav-link" to="/users/">command product</Link>
                                    </li>
                                    <li class="nav-item">
                                        <Link class="nav-link" to="/">Home</Link>
                                    </li>

                                </ul>
                            </div>
                        </nav>

                        <Route path="/uploadProduct" exact component={UploadProduct} />
                        <Route path="/about/" exact component={UploadProduct} />
                        <Route path="/users/" exact component={UploadProduct} />
                        <Route path="/"exact  component={Home} />

                    </div>
                </Router>
                <div class="container">

                    {this.state.product.length > 0 ? <div className="row">
                        {this.state.product.map((prod) => {
                            var d = "http://localhost:8080/image/" + prod.img1
                            return (
                                <div class="card" class="r">
                                    <img class="card-img-top" src={d} alt="Card image cap" />
                                    <div class="card-body">
                                        <h2 class="card-title">{prod.titre}</h2>
                                        <p class="card-text">{prod.description}</p>
                                        <p class="card-text">{prod.prix} $</p>
                                        <a href="#" class="btn btn-primary">Ajouter panier</a>
                                    </div>
                                </div>
                            )
                        })}
                    </div> : ""}





                </div>
            </div>

        )
    }
}
